mapper.select:
SELECT BUSINESS_NO
FROM SELLER_INFO
WHERE USER_NO = #{userNo}

	 	SELECT seller_page_no
          FROM SELLER_PAGE
         WHERE BUSINESS_NO = #{businessNo}

mapper.insert:
INSERT INTO SELLER_PAGE ( business_no, seller_explain, sp_enroll_Date, sp_origin_name, sp_change_name
) VALUES ( #{businessNo}, #{sellerExplain}, default, #{spOriginName}, #{spChangeName}
)

mapper.update:
UPDATE SELLER_PAGE
   SET SELLER_EXPLAIN = #{sellerExplain},
   	   SP_ENROLL_DATE = CURRENT_TIMESTAMP,
   	   SP_ORIGIN_NAME = #{spOriginName},
   	   SP_CHANGE_NAME = #{spChangeName}
 WHERE BUSINESS_NO = #{businessNo}

mapper.insert:
INSERT INTO 
			PRODUCT_CATEGORY (
						 	  p_category,
						 	  seller_page_no
						 	  )
				 	  VALUES (
					 		  #{pdCategory}, 
					 		  #{sellerPageNo}
				 		 	  )

mapper.update:
UPDATE PRODUCT SET P_TITLE = #{pdTitle},
P_ORIGIN_NAME = #{pdOriginName}, P_CHANGE_NAME = #{pdChangeName},
P_CONTENT = #{pdContent} WHERE P_NO = #{pno}

UPDATE PRODUCT
   SET P_STATUS = 'N' 
 WHERE P_NO = #{pno}
